import React, { Component } from 'react';
import { Grid, Image, Header, Dropdown } from 'semantic-ui-react'
import { withRouter } from "react-router-dom";
import VariantTable from '../components/VariantTable/VariantTable';
import SearchIcon from '../components/SearchIcon/SearchIcon';

// components
import API from '../util/API';


class Home extends Component {
	state = {
		isLoading: false,
		autosuggestOptions: [],
		variantData: null
	}

	searchByGene = () => {
		this.setState({isLoading: true});
		API.variantTable().getGene(this.state.searchGeneName).then(response => {
			this.setState({
				isLoading: false,
				variantData: response.data.variant_data 
			});
		})
	}

	autocomplete = (char_) => {
		API.variantTable().autocomplete(char_).then(response => {
			this.setState({
				autosuggestOptions: response.data.autosuggest_genes.map((gene) => {
					return {key: gene, text: gene, value: gene}
				})
			})
		});
	}

  handleClick = (e) => {
  	if (!e.target.value) {
  		this.setState({searchGeneName: e.target.value}, () => {
  			this.searchByGene();
  		});
  	} else {
  		this.searchByGene();
  	}
  }

  handleKeyPress = (e) => {
  	// console.log(e.nativeEvent);
  	// console.log(e.target);

  }

  handleKeyUp = (e) => {
  	console.log(e.key);
  	console.log(e.nativeEvent.srcElement.value);
  	console.log(e.target);;
  	// this.autocomplete(e.target.value);	
  	if (e.key === 'Enter') {
  		if (!e.target.value) {
				this.setState({searchGeneName: e.target.value}, () => {
					this.searchByGene();
				});
			} else {
				this.searchByGene();
			}
  	}
  }

	render() {
		const { isLoading, variantData, autosuggestOptions } = this.state;

		return (
			<Grid>
			  <Grid.Row>
			    <Grid.Column width={1}/>
			    <Grid.Column width={14}>
			    	<Grid>
							<Grid.Column width={4}>
								<Image src='/public/invitae_logo.png' size='massive'/>
							</Grid.Column>
							<Grid.Column width={12}>
							  <Grid.Row>
									<Header as='h1' style={{fontFamily: 'ScalaSansWeb',
							                            color: '#525252',
							                            padding: '9px 17px 6px 9px'}}>
							      Variant Search
							    </Header>
							  </Grid.Row>
							  <Grid.Row>
							  	<Dropdown fluid 
							  	          multiple 
							  	          search 
							  	          selection 
							  	          clearable 
							  	          loading={isLoading}
							  	          placeholder='Search for genes'
							  	          options={autosuggestOptions}
							  	          icon={<SearchIcon handleClick={this.handleClick}/>}
							  	          onKeyPress={(e) => this.handleKeyPress(e)}
							  	          onKeyUp={(e) => this.handleKeyUp(e)}/>
							  </Grid.Row>
							</Grid.Column>
						</Grid>
			    </Grid.Column>
			    <Grid.Column width={1}/>
			  </Grid.Row>
			  <Grid.Row>
			    <Grid.Column width={16}>
			      <VariantTable variantData={variantData}/>
			    </Grid.Column>
	      </Grid.Row>
      </Grid>
		)
	}
}

export default withRouter(Home);
